classdef MeshVisualizationTool < handle

    % MeshVisualizationTool
    %
    % A tool for visualizing finite element meshed generated by Zeffiro
    % Interface.

    properties

        zef app.Zef

        window matlab.ui.Figure

        % Other properties here.

    end % properties

    methods

        function self = MeshVisualizationTool(zef, varargin)

            self.zef = zef;

            self.window = figure('MenuBar','none', 'NumberTitle', 'off', ...
                'Name', 'Zeffiro Interface: Mesh Visualization Tool');

        end

    end % methods

end % MeshVisualizationTool
